INSERT INTO "pages" ("id", "title", "slug", "content", "description", "is_published", "meta_title", "meta_description", "created_by", "updated_by", "created_at", "updated_at") VALUES (10, 'About Us', 'about', '<div class="container mx-auto px-4 py-8">
    <h1 class="text-4xl font-bold mb-6">About Poultry Gear</h1>
    <div class="prose max-w-none">
      <p>Welcome to Poultry Gear, your trusted source for high-quality poultry farming equipment since 2005.</p>
      
      <p>At Poultry Gear, we understand the challenges and requirements of modern poultry farming. Our mission is to provide innovative, durable, and efficient equipment that helps farmers improve productivity while maintaining poultry health and welfare.</p>
      
      <h2>Our Story</h2>
      
      <p>Poultry Gear was founded by a team of agricultural engineers and experienced poultry farmers who saw a need for better equipment in the industry. What began as a small workshop developing specialized feeders has grown into a comprehensive supplier of poultry farming solutions.</p>
      
      <h2>Our Commitment</h2>
      
      <p>We are committed to:</p>
      
      <ul>
        <li><strong>Quality:</strong> Using premium materials and rigorous testing to ensure our products last</li>
        <li><strong>Innovation:</strong> Continuously improving our designs based on research and customer feedback</li>
        <li><strong>Sustainability:</strong> Developing equipment that promotes efficient resource use and reduces waste</li>
        <li><strong>Customer Support:</strong> Providing expert advice and responsive service to our clients</li>
      </ul>
    </div>
  </div>', 'Learn about Poultry Gear and how we started', TRUE, 'About Us - Poultry Gear', 'Learn about Poultry Gear and how we started', 1, 1, '2025-04-15T04:14:05.749Z', '2025-04-15T04:14:05.749Z');
INSERT INTO "pages" ("id", "title", "slug", "content", "description", "is_published", "meta_title", "meta_description", "created_by", "updated_by", "created_at", "updated_at") VALUES (11, 'FAQ', 'faq', '<div class="container mx-auto px-4 py-8">
    <h1 class="text-4xl font-bold mb-6">Frequently Asked Questions</h1>
    <div class="prose max-w-none">
      <div class="space-y-6">
        <div>
          <h3 class="text-xl font-semibold">What types of equipment do you offer?</h3>
          <p>We offer a complete range of poultry farming equipment including feeders, waterers, nesting boxes, egg collection systems, incubators, brooders, and housing solutions.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">Do you offer international shipping?</h3>
          <p>Yes, we ship to countries across the Middle East, Africa, and parts of Asia. Shipping costs and delivery times vary by location.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">What payment methods do you accept?</h3>
          <p>We accept major credit cards, PayPal, bank transfers, and cash on delivery in select regions.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">Do you offer installation services?</h3>
          <p>Yes, we offer professional installation services for large equipment and complete systems within the UAE and neighboring countries.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">What warranty is included with your products?</h3>
          <p>Most of our products come with a 1-year warranty against manufacturing defects. Premium and commercial-grade equipment may include extended warranties.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">Can I return products if they don''t meet my needs?</h3>
          <p>We offer a 30-day return policy for most unused items in their original packaging. Custom or specialized equipment may have different return terms.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">Do you offer consultation for new poultry farms?</h3>
          <p>Yes, our team of experienced poultry specialists can provide consultation services for farm setup, equipment selection, and optimization.</p>
        </div>
        
        <div>
          <h3 class="text-xl font-semibold">How can I track my order?</h3>
          <p>Once your order ships, you will receive a tracking number via email that allows you to monitor the delivery status.</p>
        </div>
      </div>
    </div>
  </div>', 'Frequently asked questions about our products and services', TRUE, 'FAQ - Poultry Gear', 'Frequently asked questions about our products and services', 1, 1, '2025-04-15T04:14:35.448Z', '2025-04-15T04:14:35.448Z');
